    //             className={
    //               classNames(
    //                 'header-nav',
    //                 isActive && 'is-active'
    //               )}>
    //             <div className="header-nav-inner">
    //               <ul className={
    //                 classNames(
    //                   'list-reset text-xs',
    //                   navPosition && `header-nav-${navPosition}`
    //                 )}>
    //                 <li>
    //                 <Link to={{ pathname: "https://docs.smartfinance.exchange/" }} target="_blank" onClick={closeMenu}>Docs</Link>
    //                 </li>                    
    //               </ul>
    //               <ul className={
    //                 classNames(
    //                   'list-reset text-xs',
    //                   navPosition && `header-nav-${navPosition}`
    //                 )}>
    //                 <li>
    //                 <Link to={{ pathname: "https://smartfinance.exchange/mint" }} target="_blank" onClick={closeMenu}>Mint</Link>
    //                 </li>                    
    //               </ul>
    //               {!hideSignin &&
    //                 <ul
    //                   className="list-reset header-nav-right"
    //                 >
    //                   <li>
    //                     { /* <Link to="#0" className="button button-primary button-wide-mobile button-sm" onClick={closeMenu}>Sign up</Link> */ }
    //                     <Link to={{ pathname: "https://smartfinance.exchange/swap" }}  className="button button-primary button-wide-mobile button-sm" target="_blank" > Launch Dapp</Link>
    //                   </li>
    //                 </ul>}
    //             </div>
    //           </nav>
    //         </>}
    //     </div>
    //   </div>
    // </header>
  );
}

Header.propTypes = propTypes;
Header.defaultProps = defaultProps;

export default Header;
