import React from 'react'
import styled from 'styled-components'
import { Text, Flex, IconButton, ArrowBackIcon, Button } from 'toolkit/uikit'
import { Link } from 'react-router-dom'
import Settings from './Settings'
// import Transactions from './Transactions'
import QuestionHelper from '../QuestionHelper'
import "./header.css"

interface Props {
  title: string
  subtitle: string
  helper?: string
  backTo?: string
  noConfig?: boolean
  swapType?: number
  setSlippage?: (value: number | ((prevVar: number) => number)) => void;
  setSwapType?: (value: number | ((prevVar: number) => number)) => void;
}

const AppHeaderContainer = styled(Flex)`
  align-items: start;
  justify-content: center;
  // padding: 24px;
  padding-bottom: 1px;
  width: 100%;
  color: black;
  background-color: #1B2028 !important;
  // border-bottom: 1px solid ${({ theme }) => theme.colors.cardBorder};
`

const AppHeader: React.FC<Props> = ({ title, subtitle, helper, backTo, noConfig = false, setSlippage, setSwapType, swapType }) => {
  return (
    <AppHeaderContainer>
      <Flex alignItems="start" justifyContent="start" mr={noConfig ? 0 : '10px'} >
          <Button onClick={()=> { setSwapType(1) }} style={{backgroundImage: `${swapType === 1 ? "linear-gradient(90deg, #0095EC -34.58%, #154DDD 37.78%, #154DDD 92.55%)" : "linear-gradient(90deg, #272F37 -34.58%, #272F37 37.78%, #272F37 92.55%)"} `, color: `${swapType === 1 ? "white" : 'rgba(255, 255, 255, 0.5)'}`}} className ="button3" >{title}</Button>
          <Button onClick={()=> {
            setSwapType(2)
          }} className ="button4" style={{backgroundImage: `${swapType === 2 ? "linear-gradient(90deg, #0095EC -34.58%, #154DDD 37.78%, #154DDD 92.55%)" : "linear-gradient(90deg, #272F37 -34.58%, #272F37 37.78%, #272F37 92.55%)"} `, color: `${swapType === 2 ? "white" : 'rgba(255, 255, 255, 0.5)'}`}}>{subtitle}</Button>
        { /* <Text mb="2px" fontFamily="Teko" color="black" fontSize="16px !important">{title}</Text> */ }
        </Flex>
      {!noConfig && (
         <Flex style={{position:'absolute', right:'32px', top:'37px'}}>
          <Settings setSlippage={setSlippage} />
          {/* <Transactions /> */}
        </Flex>
      )}
    </AppHeaderContainer>
  )
}

export default AppHeader
