const Cards = styled(BaseLayout)`
  align-items: stretch;
  justify-content: stretch;
  margin-bottom: 24px;
  grid-gap: 24px;
  width: 100%;


  & > div {
    grid-column: span 12;
    background-color: #191b1f; 
  }

