            <Labelm labelSize='12px'>Mint Price: 13.50 AVAX</Labelm>        
            <Labelm labelSize='14px'>Max 25 NFTs Per TX</Labelm>
            <StyledButton onClick={buyNft}>Mint</StyledButton>  
          </StyledCardBody>
      </AppBody>
      </BiggerCards>
    </Page >
    </MobileView>
    </>

  )
}

export default BuyNode
