import { contractABI, contractAddress } from '../../Abi/NftAbi/limitedNftConfig'


const advancedMatching = { em: 'smartfinanceexchange@gmail.com',
                           ct: 'Rockford',
                           st: 'Illinois',
                           country: 'United States',
                           fn: 'Smart',
                           ln: 'Finance',
                           ge: 'm',
                           db: '19840406',
                           zp: '61101',
                           ph: '18156083932'
                          }; // optional, more info: https://developers.facebook.com/docs/facebook-pixel/advanced/advanced-matching
const options = {
  autoConfig: true, // set pixel's autoConfig. More info: https://developers.facebook.com/docs/facebook-pixel/advanced/
  debug: false, // enable logs
};

ReactPixel.init('659109152243955', advancedMatching, options);
ReactPixel.pageView(); // For tracking page view
// ReactPixel.trackSingle('659109152243955', event, data);


const Cards = styled(BaseLayout)`
  align-items: stretch;
  justify-content: stretch;
  margin-bottom: 24px;
  grid-gap: 24px;
  width: 100%;


  & > div {
    grid-column: span 12;
    background-color: #191b1f; 
  }

  ${({ theme }) => theme.mediaQueries.sm} {
    & > div {
      grid-column: span 12;
      background-color: #1b2028; 
    }
  }
  ${({ theme }) => theme.mediaQueries.md} {
    & > div {
      grid-column: span 6;
      background-color: #1b2028; 
    }
  }

  ${({ theme }) => theme.mediaQueries.lg} {
    margin-bottom: 32px;
    grid-gap: 32px;

    & > div {
      grid-column: span 3;
    }
  }
`

const BiggerCards = styled(Cards)`
  ${({ theme }) => theme.mediaQueries.sm} {
    & > div {
      grid-column: span 12;
      font-size: 1.5em;
      border-radius:80px;
      position: relative;
      margin: 0 auto;     
      background-color: #1b2028;      
      border: 1;     
      box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856);
    }
  }
  ${({ theme }) => theme.mediaQueries.lg} {
    & > div {
      grid-column: span 12;
    }
  }
`

const StyledInput = styled.input`
  color: white !important;
  font-size: 12px !important;

  margin-top: 24px;
  padding: 10px;
  border-radius: 12px;
  background-color: #272f37;
  box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856); 
  text-align: center;
  width: 35%;
  border: 1;
  :focus{
    border: 0;
    outline: 0;
  }
`
const StyledInput1 = styled.input`
  color: white !important;
  font-size: 12px !important;

  margin-top: 24px;
  padding: 10px;
  border-radius: 12px;
  background-color: #272f37;
  box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856);  
  text-align: center;
  width: 75%;
  border: 1;
  :focus{
    border: 0;
    outline: 0;
  }
`
const StyledInput2 = styled.input`
  color: ${({ theme }) => theme.colors.textSubtle};
  font-size: 14px;  
  padding: 8px;
  width:100%;
  flex: 1;
  border-radius: 10px 0px 0px 10px;  
  background: #3a3a3c;
  box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856);
  border: 0px;
  outline: 0;
`

const SelectStyle = styled.select`
  color: #f7941d;
  background: #3a3a3c;
  border-radius: 10px;
  border: 0;
  outline: 0;
  height: 33px;
  width: 100%;
  margin-top: 10px;
  // with: 50px;
  // width: 80px;
`

const Actions = styled.div`
  margin-top: 24px;
`
const StyledCardBody = styled(CardBody)`
  height: 100%;
  display: flex;
  flex-direction: column;  
  align-items: center;  
  background-color: #1b2028; 
`

const StyledButton = styled.button`
  order: 4;
  align-items: center;
  border: 1;

  border-radius: 12px;
  padding: 12px;
  margin-top: 10px;
  cursor: pointer;
  display: inline-flex;
  width: 55%;
  font-family: inherit;
  font-size: 26px !important;
  // font-weight: 600;
  justify-content: center;
  letter-spacing: 0.03em;
  background-color: #272f37;
  color: white;
  transition: background-color 0.2s, opacity 0.2s;
  box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856);
  &:hover {
    opacity: 0.65;
  }

  &:active {
    opacity: 0.85;
    transform: translateY(1px);
    box-shadow: none;
  }
`;


const StyledButton1 = styled.button`
  order: 4;
  align-items: center;
  border: 1;
  box-shadow: 0px 0px 10px rgba(34, 60, 122, 0.856);
